import React, { useState } from "react";
import "./App.css";


function App() {
  const [temp, setTemp] = useState("");
  const [desc, setDesc] = useState("");
  const [icon, setIcon] = useState("");
  const [country, setCountry] = useState("");
  const [city, setCity] = useState("");
  const [sky, setSky] = useState("");
  const [sunrise, setSunrise] = useState("");
  const [sunset, setSunset] = useState("");
  const [wind, setWind] = useState("");
  const [pressure, setPressure] = useState("");
  const [humidity, setHumidity] = useState("");
  const [isReady, setReady] = useState(false);

  React.useEffect(() => {
    fetch(
    "https://api.openweathermap.org/data/2.5/weather?lat=14.6937&lon=-17.4441&appid=fb9038f1c48d0999349a1882426634d9&units=metric"
    )
    .then(result => result.json())
    .then(jsonresult => {
      setCountry(jsonresult.sys.country);
      setCity(jsonresult.sys.name);
      setSky(jsonresult.weather[0].main);
      setDesc(jsonresult.weather[0].description);
      setIcon(jsonresult.weather[0].icon);
      setWind(jsonresult.wind.speed);
      setTemp(jsonresult.main.temp);
      setPressure(jsonresult.main.pressure);
      setHumidity(jsonresult.main.humidity);
      setSunrise(jsonresult.sys.sunrise);
      setSunset(jsonresult.sys.sunset);
      setReady(true);
    })
    .catch(err => console.error(err));
  }, []);


  if (isReady) {
    return (
      <div className="App">
        <p>Temperature: {temp} Â°C</p>
        <p>Description: {desc}</p>
        <img
          src={`http://openweathermap.org/img/wn/${icon}@2x.png`} alt="Weather icon"
        />
      </div>
    );
  } 
  else {
    return <div>Loading...</div>;
  };

}
export default App;
