import React, { useState } from "react";
import "./App.css";


function Background (props)
{
    const [time, setTime] = useState(true);
    const [sky, setSky] = useState("1");

    const handleBackground = () =>
    {
        if ((props.time>="7")&&(props.time<="19")) {
            if (props.sky==="Clouds")
            {
                setSky("2");
            }
            else if (props.sky==="Rain")
            {
                setSky("3");
            }
            else if (props.sky==="Clear")
            {
                setSky("1");
            }
            setTime(true);
        }
    }

    return (
        <div className="principal" style={{backgroundColor:"gold",}}>
            <p><strong> Code Pays: {props.country}<br/>
                Ville: {props.city} <br/><br/><br/>
                Ciel: {props.sky} <br/>
                Description: {props.desc} 
                <img src={`http://openweathermap.org/img/wn/${props.icon}@2x.png`} alt="Weather icon"
                />
                <br/><br/>
                Vitesse du Vent: {props.wind} <br/>
                Temperature: {props.temp} °C <br/>
                Pression: {props.pressure} <br/>
                Humidité: {props.humidity} <br/>
                Heure actuelle: {props.time} <br/>
                Levée: {props.sunrise} <br/>
                Couchée: {props.sunset}
                </strong>
            </p>
        </div>
    );
}

export default Background;
