import React, { useState } from "react";
import "./App.css";
import App from "./App";
import day_light from "./img/day_light.jpeg"
import day_clouds from "./img/day_clouds.jpeg"
import day_raining from "./img/day_raining.jpeg"
import night_light from "./img/night_light.jpeg"
import night_rain from "./img/night_rain.jpeg"
import night_clouds from "./img/night_clouds.jpeg"


function Background ({data})
{
    const [time, setTime] = useState(true);
    const [sky, setSky] = useState("1");

    const handleBackground = () =>
    {
        if ((data.time>data.sunrise)&&(data.time<data.sunset)) {
            if (data.sky==="Clouds")
            {
                setSky("2");
            }
            else if (data.sky==="Rain")
            {
                setSky("3");
            }
            else if (data.sky==="Clear")
            {
                setSky("1");
            }
            setTime(true);
        }
        else{
            if (data.sky==="Clouds")
            {
                setSky("2");
            }
            else if (data.sky==="Rain")
            {
                setSky("3");
            }
            else if (data.sky==="Clear")
            {
                setSky("1");
            }
            setTime(false);
        }
    }

    if (time)
    {
        if (sky==="1")
        {
            return (
                <div className="principal" style={{backgroundImage:`url(${day_light})`}}>
                    <p><strong> Code Pays: {data.country}<br/>
                        Ville: {data.city} <br/><br/><br/>
                        Ciel: {data.sky} <br/>
                        Description: {data.desc} 
                        <img src={`http://openweathermap.org/img/wn/${data.icon}@2x.png`} alt="Weather icon"
                        />
                        <br/><br/>
                        Vitesse du Vent: {data.wind} <br/>
                        Temperature: {data.temp} °C <br/>
